  padding: 8px 16px;

  & p {
    margin: 0;
  }

  &__wrapper {
    @include flex(normal, center);
    padding: 8px 16px;
    margin: 0;
    border: 2px solid #fff;
    background: linear-gradient(90deg, $color-primary 0%, $color-secondary 80%);
  }

  &__title {
    position: absolute;
    top: -12px;
