// Screenreaders
.sr-only {
  border: 0;
  clip: rect(0 0 0 0);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  width: 1px;
  white-space: nowrap; /* 1 */
  &:active, &:focus {
    clip: auto;
    height: auto;
    margin: 0;
    overflow: visible;
    position: static;
    width: auto;
    white-space: inherit;
  }
}

sources.css/_header.scss:
.header {
  margin: 0 auto;
  @include flex(space-between);
  @include rwd(tablet) {
  }
    @include rwd('desktop') {
    max-width: map-get($breakpoints, 'desktop');
    min-height: 100px;
  }

  & .logo {
    @include rwd(mobile) {
      display: inline-block;
      max-height: 50px;
      padding: 8px;
    }
    @include rwd(tablet) {
      max-height: 80px;
      padding: 0 8px;
      margin: 16px 0;
    }
    @include rwd(desktop) {
      padding: 8px 0;
    }
  }
}

.nav__item--radius {
  @include rwd(mobile) {
    border-bottom-left-radius: 0;
  }
  @include rwd(tablet) {
    border-bottom-left-radius: 16px;
  }
}

.nav {
  background-image: $color-primary;
  background: linear-gradient(90deg, $color-secondary 50%, $color-primary 50%);
  color: #fff;

  &__wrapper {
    @include flex(space-between, center);
    margin: 0 auto;
    @include rwd('desktop') {
      max-width: map-get($breakpoints, 'desktop');
    }

    &__inner {
      align-self: flex-start;
      @include rwd(mobile) {
        width: 100%;
      }
      @include rwd(tablet) {
        width: auto;
      }
    }
  }

  &__container {
    @include flex();
    padding: 0;
    margin: 0;
    list-style: none;
    @include rwd(mobile) {
      justify-content: center;
      flex-wrap: wrap;
    }
    @include rwd(tablet) {
      flex-wrap: nowrap;
    }
  }


  &__link {
    position: relative;
    display: inline-block;
    padding: 8px 16px;
    text-decoration: none;
    color: #fff;

    &::after {
      content: '';
      position: absolute;
      bottom: 16px;
      left: 0;
      display: none;
      height: 2px;
      width: 90%;
      background: #fff;
      transform: translateX(5%);

    }

    &:hover::after, :focus::after {
      @include rwd(desktop) {
        display: block;
      }
    }
  }

  &--primary {
    @include rwd(mobile) {
      background: $color-primary;
    }
    @include rwd(tablet) {
      background: transparent;
    }

    & .nav__item {
      position: relative;
      background: $color-primary;
    }

    & .nav__link {
      padding: 24px 16px;
      font-size: 14px;
      text-transform: uppercase;

      &:active + .nav__container__inner, &:focus + .nav__container__inner {
        @extend .nav__container;
        display: block;
      }
    }
  }

  &--secondary {
    padding-left: 16px;
    background: $color-secondary;
    font-weight: bold;
    text-transform: capitalize;
    @include rwd(mobile) {
      justify-content: normal;
    }
    @include rwd(tablet) {
      justify-content: space-around;
      border-top-right-radius: 16px;

    }
    @include rwd(desktop) {
      margin-right: -16px;
    }

    & .nav__item {
      @include rwd(mobile) {
        flex-basis: calc(50% - 16px);
        text-align: center;
      }
      @include rwd(tablet) {
        flex-basis: calc(25% - 16px);
        text-align: left;
      }

    }

    & .nav__link {
      @include rwd(tablet) {
        @include flex(center, center);

      }
      @include rwd(tablet) {
        justify-content: normal;
      }
      @include rwd(desktop) {
        padding: 42px 0;
        &::after {
          bottom: 24px;
        }

      }

      & .fa {
        @include rwd(mobile) {
          display: none;

        }
        @include rwd(desktop) {
          margin-right: 8px;
        }
        font-size: 32px;
      }
    }
  }

  &__container__inner {
    display: none;
    position: absolute;
    bottom: 0;
    width: 100%;
    transform: translateY(100%);
    z-index: 2;

    & .nav__link__inner {
      padding: 16px 0;
    }
  }

  &__link__inner {
    @extend .nav__link;
    width: 100%;
    background: $color-primary;
    text-align: center;

    &::after {
      bottom: 4px;
    }
  }
}

@include rwd(mobile) {
  .card {
    &--mobile {
      @include flex(normal, center)
    }

    &__link--mobile {
      display: inline-block;
    }

    &__photo--mobile {
      max-height: 48px;
      border-radius: 50%;
    }
  }
}

@include rwd(tablet) {
  .card {
    &--mobile {
      display: none;
    }
  }
}

.card {
  @include rwd(mobile) {
    display: none;
  }
  @include rwd(tablet) {
    position: absolute;
    top: 8px;
    right: 0;
    display: block;
  }
  @include rwd(desktop) {
    position: relative;
    margin: 16px auto 0 56px;
    padding: 8px 16px;
    margin: 16px auto 0 56px;

  }
  position: relative;
  padding: 8px 16px;

  & p {
    margin: 0;
  }

  &__wrapper {
    @include flex(normal, center);
    padding: 8px 16px;
    margin: 0;
    border: 2px solid #fff;
    background: linear-gradient(90deg, $color-primary 0%, $color-secondary 80%);
  }

  &__title {
    position: absolute;
    top: -12px;
    left: 24px;
    font-size: 12px;
    font-weight: bold;
    margin: 0;
  }


  @include rwd(desktop) {
    &__photo {
      margin: 16px 0 16px 24px;
    }
  }

  &__photo {

    &__item {
      max-width: 64px;
      max-height: 64px;
      border-radius: 50%;
    }
  }

  &__info {
    margin-left: 24px;
    font-size: 12px;

    &__name {
