.research {
  @include rwd(desktop) {
    max-width: 1004px;
    margin: 48px auto;
  }

  &__header {
    border-bottom: 1px solid $color-accent;
  }

  &__title {
    padding: 0 8px;
    font-weight: normal;
    font-size: 36px;
    color: $color-primary;

    & > cite {
      font-style: normal;
    }

    &--secondary {
      padding: 0 4px;
    }
  }

  &__link {
    font-size: 24px;
    color: $color-accent;
    text-decoration: none;

    &:hover {
      text-decoration: underline;
    }
  }

  &__info {
    &__wrapper {
      padding: 16px 8px 24px;
    }

    &__container {
      @include flex();

      & p {
        margin: 0;
      }

    }

    &__label {
      font-weight: bold;
      flex-basis: 18%;
    }
  }
}

@include rwd(mobile) {
  .research__info {
    &__container {
      justify-content: space-between;
    }
    &__container  &__item {
      margin:0 16px 8px;
    }
    &__item {
      text-align: right;
    }
  }
}
@include rwd(tablet) {
  .research__info {
    &__container {
      justify-content: normal;
    }
  }
}

sources.css/_footer.scss:
.footer {
  background: $color-footer;

  &__wrapper {
    @include flex(space-between, center);
    flex-wrap: wrap;
    margin: 0 auto;
    padding: 16px 4px;
    max-width: map-get($breakpoints, desktop);
    @include rwd(desktop) {
      padding: 48px 0;
    }
  }

  &__link {
    @extend .nav__link;
    padding: 0;
    text-decoration: underline;

    &:hover::after {
      display: none;
    }
  }

  &__img {
    max-width: 200px;
  }
}

@include rwd(mobile) {
  .footer {
    min-height: auto;
  }
  .footer__img {
    margin: 16px;
    margin-top: 64px;
  }
}
@include rwd(desktop) {
  .footer {
    min-height: 250px;
    margin-top: 128px;
    &__img {
      margin: 0;
    }
  }
}
